import SVGService from "@styles/icons/icon_service.svg";

const imageUrl: string = "/icons/";

export const FixedSize = (props) => {
  let width, height;

  switch (props.size) {
    case "small":
      width = 16;
      height = 16;
      break;
    case "medium":
      width = 24;
      height = 24;
      break;
    case "large":
    default:
      width = 32;
      height = 32;
      break;
  }

  return <Image src={props.src} width={width} height={height} alt="Image" />;
};

