export const StarEffect = (props) => (
  <FixedSize src={`${imageUrl}star_effect.svg`} {...props} />
);
export const ItemTypeIcon = (props) => {
  const { type, size, ...others } = props;
  let width, height;

  switch (size) {
    case "small":
      width = 32;
      height = 32;
      break;
    case "medium":
      width = 24;
      height = 24;
      break;
    case "large":
    default:
      width = 16;
      height = 16;
      break;
  }

  return (
    <SvgIcon viewBox={`0 0 ${width} ${height}`} {...others}>
      {type.toLowerCase() === "item" ? (
        <SVGSword />
      ) : type.toLowerCase() === "service" ? (
        <SVGService />
      ) : (
        <SVGCoins />
      )}
    </SvgIcon>
  );
};

const FixedDiv = styled.div`
  background-image: url("/icons/balance_A.png");
  background-size: 100% 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  padding-bottom: 2px;
`;

interface Props {
  size: number;
}

export const Balance = (props: Props) => (
  <FixedDiv style={{ width: props.size, height: props.size }}>
    <Image
      src="/icons/balance_B.png"
      alt="balance"
      width={props.size * 0.6}
      height={props.size * 0.5}
    />
  </FixedDiv>
);
