const SellSteps = [
  "Create an account",
  'Use the "Create Listing" button to list your item(s) for sale',
  "Communicate with potential buyers who leave bids on your item(s)",
  "After accepting an offer, conduct the trade. Note: it's important to take screenshots of the transaction",
  'Upload your trade screenshots and hit "Mark Trade Complete" on the "Past and Pending Trades" page',
  "Congratulations, you just completed your trade! Any Charsi Bucks owed to you will be transferred to your account -- from the buyer -- once the buyer also confirms Trade Completion",
  "Optional: leave a review for the buyer",
];

const BuySteps = [
  "Create an account",
  "Search for the item you want to buy; utilize the game category pages or the search bar at the top to find the item(s) you desire",
  'Place a bid -- or select a "Buy Now" option',
  "Await for the seller to send you a message (or, you can also send the seller a message via the Chat icon in the header)",
  "Conduct the trade. Taking screenshots of the transaction is advised in the event of a future dispute",
  'Once you received the item(s), "Mark Trade Complete" on the  "Past and Pending Trades" page',
  "Optional: leave a review for the seller",
];

export default function About() {
  const theme = useTheme();

  return (
    <>
      {/* Icons Section */}
      <Container maxWidth="lg" sx={{ mt: 5, textAlign: "center" }}>
        <Typography
          variant="h1"
          sx={{
            background: `-webkit-linear-gradient(45deg, #7C4DFF 0%, #FB4DFF 100%)`,
            WebkitBackgroundClip: "text",
            backgroundClip: "text",
            WebkitTextFillColor: "transparent",
          }}
        >
          Built with gamers in mind.
        </Typography>
        <Grid container spacing={3} sx={{ mt: 3 }}>
          {AboutSections.map((section, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Box sx={{ py: 4 }}>
                <Typography variant="h2" color="primary" sx={{ mb: 2 }}>
                  {section.icon}
                </Typography>
                <Typography variant="h5" color="textPrimary" sx={{ mb: 2 }}>
                  {section.heading}
                </Typography>
                <Typography variant="body1" color="textPrimary">
                  {section.text}
                </Typography>
              </Box>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* How To Section */}
      <Box>
        <Container maxWidth="lg" sx={{ py: 5 }}>
          <Grid container spacing={6}>
            <Grid item xs={12} md={6}>
              <Typography
                variant="h4"
                gutterBottom
                color={theme.palette.primary.main}
              >
                How to Sell
              </Typography>
              <List sx={{ pl: 1 }}>
                {SellSteps.map((step, index) => (
                  <ListItem key={index}>
                    <Typography variant="h6">{step}</Typography>
                  </ListItem>
                ))}
              </List>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography
                variant="h4"
                gutterBottom
                color={theme.palette.primary.main}
              >
                How to Buy or Trade
              </Typography>
              <List sx={{ pl: 1 }}>
                {BuySteps.map((step, index) => (
                  <ListItem key={index}>
                    <Typography variant="h6">{step}</Typography>
                  </ListItem>
                ))}
              </List>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* FAQ Section */}
      <Container maxWidth="lg" sx={{ my: 5 }}>
        <Typography variant="h3" mb={4}>
          FAQ
        </Typography>
        {FaqSections.map((_, index) => (
          <Accordion key={index} sx={{ mt: 2 }}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography
                variant="h6"
                sx={{ display: "flex", alignItems: "center" }}
              >
                {_.icon} <Box sx={{ ml: 1 }}>{_.heading}</Box>
              </Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography variant="body1">{_.text}</Typography>
            </AccordionDetails>
          </Accordion>
        ))}
      </Container>
    </>
  );
}

About.getLayout = (page) => <DefaultLayout>{page}</DefaultLayout>;
